msg.label = msg.payload ? "Stop" : "Start";
if(msg.payload){
    msg.stopTime = Date.now();
}
return msg;

let output = new Array(3);

let keyValue = msg.payload.trim().split("=");

let value = keyValue[1];

msg.key = keyValue[0];

if(msg.key=="B0"){
    output[0] = {payload:parseInt(value)};
}
else if(msg.key=="C0"){
    vals = value.split(" ");
    output[1] = {payload:parseInt(vals[0])};
    output[2] = {payload:parseInt(vals[1])};
}

return output;
